/*https://pandemicdatalake.blob.core.windows.net/public/curated/covid-19/bing_covid-19_data/latest/bing_covid-19_data.parquet*/

/*Query1*/
SELECT TOP 100 * FROM
    OPENROWSET(
        BULK 'https://pandemicdatalake.blob.core.windows.net/public/curated/covid-19/bing_covid-19_data/latest/bing_covid-19_data.parquet',
        FORMAT='PARQUET'
    ) AS [nyc];


/*Qury2*/
SELECT
    YEAR(updated) AS year,
    MONTH(updated) as month,
    COUNT(*) AS cases
FROM
    OPENROWSET(
        BULK 'https://pandemicdatalake.blob.core.windows.net/public/curated/covid-19/bing_covid-19_data/latest/bing_covid-19_data.parquet',
        FORMAT='PARQUET'
    ) AS [nyc]
WHERE deaths>0
GROUP BY YEAR(updated), MONTH(updated)
ORDER BY 1 ASC;
